import React, { useEffect, useState } from "react";
import { Spinner, Table, Button } from "react-bootstrap";

const ManageBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/allBookings`)
      .then((res) => res.json())
      .then((data) => {
        setBookings(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching bookings:", error);
        setLoading(false);
      });
  }, []);

  const handleDelete = (id) => {
    const checker = window.confirm("Are you sure to delete?");
    if (checker) {
      fetch(`http://localhost:5000/deleteBooking/${id}`, {
        method: "DELETE",
        headers: { "content-type": "application/json" }
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.deletedCount) {
            setLoading(true);
            fetch(`http://localhost:5000/allBookings`)
              .then((res) => res.json())
              .then((data) => {
                setBookings(data);
                setLoading(false);
              })
              .catch((error) => {
                console.error("Error re-fetching bookings:", error);
                setLoading(false);
              });
          } else {
            setLoading(false);
          }
        })
        .catch((error) => {
          console.error("Error deleting booking:", error);
          setLoading(false);
        });
    }
  };

  const handleUpdate = (id) => {
    const url = `http://localhost:5000/bookings/${id}`;
    fetch(url, {
      method: "PUT",
      headers: {
        "content-type": "application/json"
      }
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.modifiedCount > 0) {
          alert("Update Successful");
          setLoading(true);
          fetch(`http://localhost:5000/allBookings`)
            .then((res) => res.json())
            .then((data) => {
              setBookings(data);
              setLoading(false);
            })
            .catch((error) => {
              console.error("Error re-fetching bookings:", error);
              setLoading(false);
            });
        } else {
          setLoading(false);
        }
      })
      .catch((error) => {
        console.error("Error updating booking:", error);
        setLoading(false);
      });
  };

  return (
    <div className="container">
      <h2 className="text-center blue-color">Manage all Bookings</h2>
      <div className="table-responsive">
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Tour Name</th>
              <th>Booked by</th>
              <th>Phone</th>
              <th>Address</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {isLoading ? (
              <tr>
                <td colSpan="7" className="text-center">
                  <Spinner animation="border" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </Spinner>
                </td>
              </tr>
            ) : (
              bookings.map((booking, index) => (
                <tr key={booking._id}>
                  <td>{index + 1}</td>
                  <td>{booking.tourName}</td>
                  <td>{booking.email}</td>
                  <td>{booking.phone}</td>
                  <td>{booking.address}</td>
                  <td>{booking.status}</td>
                  <td>
                    <Button onClick={() => handleUpdate(booking._id)} variant="info">
                      Update
                    </Button>{" "}
                    <Button onClick={() => handleDelete(booking._id)} variant="danger">
                      Cancel
                    </Button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </Table>
      </div>
    </div>
  );
};

export default ManageBookings;
