import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Tour.css";

const Tour = (props) => {
  const { _id, name, price, pic, details, duration } = props.tour;
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState("");
  const [photos, setPhotos] = useState([]);
  const [destination, setDestination] = useState("");

  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  const handleReviewChange = (event) => {
    setReview(event.target.value);
  };

  const handlePhotoUpload = (event) => {
    setPhotos([...photos, ...event.target.files]);
  };

  const handleDestinationChange = (event) => {
    setDestination(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission (e.g., send data to the server)
    console.log("Rating:", rating);
    console.log("Review:", review);
    console.log("Photos:", photos);
    console.log("Destination:", destination);
    // Reset form
    setRating(0);
    setReview("");
    setPhotos([]);
    setDestination("");
  };

  return (
    <div className="col-12 col-md-6 col-lg-4">
      <div className="card">
        <img src={pic} className="card-img-top" alt="..." height="200" />
        <div className="card-body">
          <h4 className="card-title text-center">{name}</h4>
          <p>{details.substr(0, 120)}</p>
          <h6 className="mb-3">
            <i className="fas fa-clock blue-color"></i> Duration: {duration} days
          </h6>
          <h6 className="mb-3">
            <i className="fas fa-hand-holding-usd blue-color"></i> Cost: ₹{price} /person
          </h6>
          <Link to={`/tour/${_id}`} className="text-decoration-none">
            <button className="btn d-block mx-auto">Book</button>
          </Link>
        </div>
      </div>
      <div className="review-section mt-4">
        <h5 className="text text-center">Add a Review</h5>
        <form onSubmit={handleSubmit}>
          <div className="rating mb-3">
            {[...Array(5)].map((star, index) => (
              <span
                key={index}
                className={index < rating ? "star filled" : "star"}
                onClick={() => handleRatingChange(index + 1)}
              >
                ★
              </span>
            ))}
          </div>
          <div className="mb-3">
            <textarea
              className="form-control"
              rows="3"
              placeholder="Write your review here..."
              value={review}
              onChange={handleReviewChange}
            ></textarea>
          </div>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Enter the destination visited"
              value={destination}
              onChange={handleDestinationChange}
            />
          </div>
          <div className="mb-3">
            <input
              type="file"
              multiple
              className="form-control"
              onChange={handlePhotoUpload}
            />
          </div>
          <button type="submit" className="btn btn-primary d-block mx-auto">
            Submit Review
          </button>
        </form>
      </div>
    </div>
  );
};

export default Tour;
