import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import "./AllReviews.css"; 

const AllReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/allReviews")
      .then((res) => res.json())
      .then((data) => {
        setReviews(data);
        setLoading(false);
      });
  }, []);

  // Function to generate star icons based on rating
  const generateStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      if (i <= rating) {
        stars.push(<span key={i} className="filled">★</span>);
      } else {
        stars.push(<span key={i}>★</span>);
      }
    }
    return stars;
  };

  return (
    <div className="my-5 black-bg"> {/* Add black-bg class */}
      <h2 className="text-center">All Reviews</h2>
      <div className="container">
        <div className="row g-3">
          {isLoading ? (
            <Spinner animation="border" role="status">
              <span className="visually-hidden">Loading...</span>
            </Spinner>
          ) : (
            reviews.map((review) => (
              <div key={review._id} className="col-12 col-md-6 col-lg-4">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Ratings:</h5>
                    <p className="card-text">
                      {generateStars(review.rating)}
                    </p>
                    <h5 className="card-title">Destination:</h5>
                    <p className="card-text">{review.destination}</p>
                    <h5 className="card-title">Review:</h5>
                    <p className="card-text">{review.reviewText}</p>
                    <h5 className="card-title">Image:</h5>
                    <img src={review.imageUrl} alt="review" style={{ width: '250px', height: '200px' }} />
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default AllReviews;
