import React, { useEffect, useState } from "react";
import { Spinner, Table, Button } from "react-bootstrap";
import useAuth from "../../Hooks/useAuth";
import logo from '../../Images/logo.png';
import jsPDF from 'jspdf';
import { applyPlugin } from 'jspdf-autotable';
applyPlugin(jsPDF);

const MyBookings = () => {
  const { user } = useAuth();
  const [bookings, setBookings] = useState([]);
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/myBookings/${user?.email}`)
      .then((res) => res.json())
      .then((data) => {
        setBookings(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching bookings:", error);
        setLoading(false);
      });
  }, [user?.email]);

  const handleDelete = (id) => {
    const checker = window.confirm("Are you sure to delete?");
    if (checker) {
      fetch(`http://localhost:5000/deleteBooking/${id}`, {
        method: "DELETE",
        headers: { "content-type": "application/json" }
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.deletedCount) {
            setLoading(true);
            fetch(`http://localhost:5000/myBookings/${user?.email}`)
              .then((res) => res.json())
              .then((data) => {
                setBookings(data);
                setLoading(false);
              })
              .catch((error) => {
                console.error("Error re-fetching bookings:", error);
                setLoading(false);
              });
          } else {
            setLoading(false);
          }
        })
        .catch((error) => {
          console.error("Error deleting booking:", error);
          setLoading(false);
        });
    }
  };

  const calculateTotalPrice = (price) => {
    const taxPercentage = 18; 
    const fixedPrice = 5000; 
    const totalPrice = fixedPrice + (fixedPrice * (taxPercentage / 100));
    return totalPrice;
  };

  const downloadInvoice = (booking) => {
    const doc = new jsPDF();

    doc.setFontSize(12);
    doc.text(20, 20, 'Invoice');

    const imgWidth = 30;
    const imgHeight = 30;
    doc.addImage(logo, 'PNG', 20, 30, imgWidth, imgHeight);

    const headers = ['Tour Name', 'Booked by', 'Phone', 'Address', 'Status', 'Price (INR)', 'Tax (%)', 'Total Price (INR)'];
    const rows = bookings.map((booking, index) => {
      const totalPrice = calculateTotalPrice(booking.price);
      return [
        booking.tourName,
        booking.email,
        booking.phone,
        booking.address,
        booking.status,
        `₹5000`,
        '18%',
        `₹${totalPrice.toFixed(2)}`
      ];
    });

    doc.autoTable(headers, rows);

    doc.save(`${booking.tourName}_Invoice.pdf`);
  };

  return (
    <div className="container">
      <h2 className="text-center blue-color">My Bookings</h2>
      <div className="table-responsive">
        {isLoading ? (
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
        ) : (
          <Table striped bordered hover>
            <thead>
              <tr>

                <th>#</th>
                <th>Tour Name</th>
                <th>Booked by</th>
                <th>Phone</th>
                <th>Address</th>
                <th>Status</th>
                <th>Action</th>
                <th>Download Invoice</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map((booking, index) => (
                <tr key={booking._id}>
                  <td>{booking.id}</td>
                  <td>{index + 1}</td>
                  <td>{booking.tourName}</td>
                  <td>{booking.email}</td>
                  <td>{booking.phone}</td>
                  <td>{booking.address}</td>
                  <td>{booking.status}</td>
                  <td>
                    <Button onClick={() => handleDelete(booking._id)} variant="danger">
                      Cancel
                    </Button>
                  </td>
                  <td>
                    <Button onClick={() => downloadInvoice(booking)} variant="success">
                      Download Invoice
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </div>
    </div>
  );
};

export default MyBookings;
