import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Tour.css";

const Tour = (props) => {
  const { _id, name, price, pic, details, duration } = props.tour;
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState("");
  const [destination, setDestination] = useState("");
  const [imageURL, setImageURL] = useState("");

  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  const handleReviewChange = (event) => {
    setReview(event.target.value);
  };

  const handleDestinationChange = (event) => {
    setDestination(event.target.value);
  };

  const handlePhotoURLChange = (event) => {
    setImageURL(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const reviewData = {
      rating: rating,
      reviewText: review,
      destination: destination,
      imageUrl: imageURL
    };

    // Send review data to the backend
    fetch("http://localhost:5000/addReview", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(reviewData)
    })
    .then(response => response.json())
    .then(data => {
      console.log("Review submitted successfully:", data);
      // Optionally, you can handle success (e.g., show a confirmation message)
    })
    .catch(error => {
      console.error("Error submitting review:", error);
      // Optionally, you can handle errors (e.g., show an error message)
    });

    // Reset form
    setRating(0);
    setReview("");
    setDestination("");
    setImageURL("");
  };

  return (
    <div className="col-12 col-md-6 col-lg-4">
      {/* Tour card content */}
      {/* Review section */}
      <div className="review-section mt-4">
        <h5 className="text text-center">Add a Review</h5>
        <form onSubmit={handleSubmit}>
          {/* Rating input */}
          {/* Review text input */}
          {/* Destination input */}
          {/* Image URL input */}
          <button type="submit" className="btn btn-primary d-block mx-auto">
            Submit Review
          </button>
        </form>
      </div>
    </div>
  );
};

export default Tour;
